xpath.script:
				$(function() {
					$('.summernote').summernote();
				    $('.multiple-tags').select2();
				    $(".input-file").fileinput({
				    	'showPreview': false,
				    	'showUpload': true,
				    });

				    $(".input-file2").fileinput({
				    	'showPreview': false,
				    	'showUpload': true,
						'uploadTitle': 'Insérer',
				    });

					$(".datecontrol").datepicker();

					$("[type='tel']").intlTelInput({
						initialCountry: "auto",
						utilsScript: "https://cdnjs.cloudflare.com/ajax/libs/intl-tel-input/12.1.9/js/utils.js",
						nationalMode: false,
						preferredCountries: ["MA"],
						geoIpLookup: function(callback) {
						  $.get("https://ipinfo.io", function() {}, "jsonp").always(function(resp) {
						    var countryCode = (resp && resp.country) ? resp.country : "MA";
						    callback(countryCode);
						  });
						}
					});
					$.validator.addMethod("intlTelNumber", function(value, element) {
					    return $(element).intlTelInput("isValidNumber");
					}, "Champ invalide");
					$('#submissionform').validate({
					    errorPlacement: function(error, element) {},
						rules: {
							phone: {
								intlTelNumber: true
							},
							mobile: {
								intlTelNumber: true
							},
							fax: {
								intlTelNumber: true
							},
							contact_phone: {
								intlTelNumber: true
							},
							inventor_phone: {
								intlTelNumber: true
							},
							inventor_mobile: {
								intlTelNumber: true
							},
							email: {
								email: true
							},
							inventor_email: {
								email: true
							},
							date: {
								date: true
							}
						},
						highlight: function (element) {
						    $(element).closest('.form-group').removeClass('has-success').addClass('has-error');
						    $(element).siblings().filter('.glyphicons').removeClass('ok_2').addClass('remove_2');

						},
						unhighlight: function (element) {
						    $(element).closest('.form-group').removeClass('has-error').addClass('has-success');
						    $(element).siblings().filter('.glyphicons').removeClass('remove_2').addClass('ok_2');

						}
					});
    				$("#submissionform").validate().form();
    				if (!Modernizr.inputtypes.date) {
						$('.date-control').datepicker({
					            dateFormat: 'yy-mm-dd'
				        });
			        }
				});
